import { Component ,ViewChildren, QueryList,Directive} from '@angular/core';
import {DataService} from '../data.service';
import { FormsModule } from '@angular/forms';
import {DropdwnItemComponent} from '../dropdwn-item/dropdwn-item.component';

@Component({
  selector: 'app-dropdown',
  templateUrl: './dropdown.component.html',
  styleUrls: ['./dropdown.component.css'],
  providers: [DataService]
})
export class DropdownComponent {
public selectedValue: any;
public searchValue ="";
public isNotSearching = false;;
data: {id: number; name: string; image: { link: string; }; groups: { id: number; name: string; url: string; }[]; url: string; }[] = [];
datas  = new Array<any>();
@ViewChildren('focusMe') focusMe: QueryList<HTMLLinkElement>;
@ViewChildren(DropdwnItemComponent) items: QueryList<DropdwnItemComponent>;



constructor(private dataService: DataService) {}
ngOnInit() {
  this.getData();
  console.log(this.items);
}


getData()
{
  this.dataService.getData().subscribe(response => {
    this.data = response;
});
}

getDataMatched(pattern: string)
{
  this.dataService.getDataMatching(pattern).subscribe(response => {
    this.data = response;
    console.log(this.data);
});
}

filterDropdown(e :any) {
  console.log(this.searchValue);

  if (this.searchValue.length >0)
  {
    this.isNotSearching = false;
    console.log(this.searchValue);
    this.getDataMatched(this.searchValue);
  }
  else {
    this.isNotSearching = true;
    this.getData();
  }

}

selectValue(name: any) {
  this.selectedValue = name; 
}

focus()
{
  console.log(this.items);

}

}
