import { Component,OnInit , Input, ViewChild} from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser'
import {NgbModal, ModalDismissReasons}   from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-dropdwn-item',
  templateUrl: './dropdwn-item.component.html',
  styleUrls: ['./dropdwn-item.component.css']
})
export class DropdwnItemComponent {

  @Input()  dataItem: any;
  @Input() isNotSearching = true;
  closeResult = '';
  newGroup: { id: number; name: string; url: string; }= { id: 0, name: '', url:''}; 

  constructor( private sanitizer: DomSanitizer, private modalService: NgbModal){

  }

  getSantizeUrl(url : string) {
    return this.sanitizer.bypassSecurityTrustUrl(url);
  }

  open(content) {
    this.modalService.open(content,
   {ariaLabelledBy: 'modal-basic-title'}).result.then((result)  => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = 
         `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }



}
